import React, { Component } from "react";
import axios from "axios";
import { FormControl, Button, Form } from "react-bootstrap";

import "./add-product.css";

class AddProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      imageUrl: "",
      description: "",
      price: 0,
    };
  }

  onAddClick = (e) => {
    e.preventDefault();

    const product = {
      name: this.state.name,
      imageUrl: this.state.imageUrl,
      description: this.state.description,
      price: this.state.price,
    };

    axios
      .post("/api/products/add", product)
      .then((res) => {
        this.setState({
          name: "",
          description: "",
          imageUrl: "",
          price: 0,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  onChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  render() {
    return (
      <>
        <section className="contact_form py-5">
          <div className="container">
            <div className="heading">
              <h1 className="heading text-capitalize mb-5 pb-3">
                <span>Добавить</span> продукт
              </h1>
            </div>
            <form noValidate onSubmit={this.onAddClick}>
              <div className="input-w3ls w3ls-left">
                <Form.Label>Наименование</Form.Label>
                <FormControl
                  type="text"
                  value={this.state.name}
                  onChange={this.onChange}
                  className="name"
                  name="name"
                  id="name"
                  placeholder="Название"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-rght">
                <Form.Label>Ссылка на картинку</Form.Label>
                <FormControl
                  type="text"
                  value={this.state.imageUrl}
                  onChange={this.onChange}
                  className="name"
                  name="address"
                  placeholder="Картинка"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-left">
                <Form.Label>Цена</Form.Label>
                <br />
                <FormControl
                  type="number"
                  value={this.state.price}
                  onChange={this.onChange}
                  className="number"
                  name="price"
                  id="price"
                  placeholder="цена"
                  min="1"
                  required
                />
              </div>
              <div className="input-w3ls">
                <FormControl
                  placeholder="Описание"
                  value={this.state.description}
                  onChange={this.onChange}
                  name="description"
                  id="description"
                  required
                  defaultValue={""}
                />
              </div>
              <div className="input-w3ls">
                <Button type="submit" disabled={!this.state.formValid}>
                  Подтвердить
                </Button>
              </div>
            </form>
          </div>
        </section>
      </>
    );
  }
}

export default AddProduct;
